mapper.update:
UPDATE NOTICE
SET NOTICE_TITLE = #{notice_title}, NOTICE_CONTENT = #{notice_content}
WHERE NOTICE_NUM = #{notice_num}

mapper.select:
SELECT NOTICE_NUM, NOTICE_TITLE, TO_CHAR(NOTICE_DATE, 'YYYY-MM-DD HH:MM') AS NOTICE_DATE, NOTICE_CONTENT
FROM NOTICE
WHERE NOTICE_NUM = #{notice_num}
